// src/form/SignupForm.tsx
import { useState } from 'react';
import {useCreateUser} from "./mutation/useCreateUser";

export const SignupForm = ({ onLoginSuccess }: { onLoginSuccess: (token: string) => void }) => {
    const [name, setName] = useState('');
    const [mail, setMail] = useState('');
    const [password, setPassword] = useState('');

    const mutation = useCreateUser();

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        mutation.mutate(
            { name, mail, password },
            {
                onSuccess: (response) => {
                    if (response.token) {
                        onLoginSuccess(response.token);
                    } else {
                        alert("Kein Token erhalten");
                    }
                },
                onError: () => {
                    alert("Signup fehlgeschlagen");
                }
            }
        );
    };

    return (
        <form onSubmit={handleSubmit}>
            <input value={name} onChange={(e) => setName(e.target.value)} placeholder="Name" />
            <input value={mail} onChange={(e) => setMail(e.target.value)} placeholder="E-Mail" />
            <input value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Passwort" type="password" />
            <button type="submit" disabled={mutation.isPending}>Signup</button>
            {mutation.isError && <p>Fehler beim Signup.</p>}
        </form>
    );
};
